    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}


{% block title 'Gérer les option' %}

{% block body %}
    <div class=\"container mt-4\">
        <h1>gérer les option</h1>

        {% for message in app.flashes('success') %}
            <div class=\"alert alert-success\">
                {{ message }}
            </div> 
        {% endfor %}
        <table class=\"table table-striped\">
            <thead>
                <tr>
                    <th>Nom</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                {% for option in options %}
                    <tr>
                        <td>{{ option.name }}</td>
                        <td>
                            <a href=\"{{ path('admin.options.edit', {id: option.id}) }}\" class=\"btn btn-secondary\">Editer</a>
                            <form method=\"POST\" action=\"{{ path('admin.options.delete',{id: option.id}) }}\" style=\"display:inline-block\" onsubmit=\" return confirm('Etes vous vraiment sur de vouloir supprimer ce bien ?')\">
                                <input type=\"hidden\" name=\"_method\" value=\"DELETE\">
                                <input type=\"hidden\" name=\"_token\" value=\"{{ csrf_token('delete' ~ option.id) }}\">
                                <button class=\"btn btn-danger\">Supprimer</button>
                            </form>
                        </td>
                    </tr>
                {% endfor %}
            </tbody>
        </table>
        <div class=\"text-right\">
            <a href=\"{{ path('admin.options.new') }}\" class=\"btn btn-primary\">Créer un nouveau bien</a>
            
        </div>
    </div>
        


{% endblock %}", "admin/options/index.html.twig", "C:\\wamp64\\www\\my-agence\\templates\\admin\\options\\index.html.twig");
